CREATE SCHEMA IF NOT EXISTS `musica` DEFAULT CHARACTER SET utf8 ;
USE `musica` ;

CREATE TABLE IF NOT EXISTS `genero` (
  `codGenero` INT NOT NULL AUTO_INCREMENT,
  `nome` VARCHAR(45) NULL,
  PRIMARY KEY (`codGenero`))
ENGINE = InnoDB;




CREATE TABLE IF NOT EXISTS `musica` (
  `codMusica` INT NOT NULL AUTO_INCREMENT,
  `nome` VARCHAR(45) NULL,
  `letra` TEXT NULL,
  PRIMARY KEY (`codMusica`))
ENGINE = InnoDB;



CREATE TABLE IF NOT EXISTS `musica_genero` (
  `codMusicaGenero` INT NOT NULL AUTO_INCREMENT,
  `codMusica` INT NOT NULL,
  `codGenero` INT NOT NULL,
  PRIMARY KEY (`codMusicaGenero`),
  INDEX `fk_musica_has_genero_genero1_idx` (`codGenero` ASC),
  INDEX `fk_musica_has_genero_musica1_idx` (`codMusica` ASC),
  CONSTRAINT `fk_musica_has_genero_musica1`
    FOREIGN KEY (`codMusica`)
    REFERENCES `musica` (`codMusica`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION,
  CONSTRAINT `fk_musica_has_genero_genero1`
    FOREIGN KEY (`codGenero`)
    REFERENCES `genero` (`codGenero`)
    ON DELETE NO ACTION
    ON UPDATE NO ACTION)
ENGINE = InnoDB;


INSERT INTO `musica` VALUES (1,'PELADOS DE SANTOS','Mina, seus cabelo é da hora\nSeu corpão violão\nMeu docinho de coco\nTá me deixando louco\n\nMinha Brasília amarela\nTá de portas abertas\nPra mode a gente se amar\nPelados em Santos\n\nPois você, minha pitchula\nMe deixou legalzão\nNão me sintcho sozinho\nVocê é meu chuchuzinho\nMusic is very good\n\n(Oxente ai, ai, ai!)\nMas comigo ela não quer se casar\n(Oxente ai, ai, ai!)\nNa Brasília amarela com roda gaúcha\nEla não quer entrar\n(Oxente ai, ai, ai!)\n\nÉ feijão com jabá\nA desgraçada num quer compartilhar\nMas ela é lindia\nMutcho mar do que lindia\nVery, very beautiful\n\nVocê me deixa doidião\nOh, yes! Oh, no!\nMeu docinho de coco\nMusic is very porreta\n\n(Oxente Paraguai!)\nPos Paraguai ela não quis viajar\n(Oxente Paraguai!)\nComprei um Reebok e uma calça Fiorucci\nEla não quer usar\n(Oxente Paraguai!)\n\nEu não sei o que faço\nPra essa mulé eu conquistchar\nPorque ela é lindia\nMutcho mar do que lindia\nVery, very beautiful\n\nVocê me deixa doidião\nOh, yes! Oh, no!\nMeu chuchuzinho\nOh, yes! No, no, no, no!\nEu te I love you!\n\nPera aí que tem mais\nUm poquinho de u\nUuuuu\n\nTchê'),(2,'HÁ TEMPOS','Parece cocaína mas é só tristeza, talvez tua cidade\nMuitos temores nascem do cansaço e da solidão\nDescompasso, desperdício\nHerdeiros são agora da virtude que perdemos\nHá tempos tive um sonho\nNão me lembro, não me lembro\n\nTua tristeza é tão exata\nE hoje o dia é tão bonito\nJá estamos acostumados\nA não termos mais nem isso\n\nOs sonhos vêm e os sonhos vão\nE o resto é imperfeito\nDisseste que se tua voz\nTivesse força igual\nÀ imensa dor que sentes\nTeu grito acordaria\nNão só a tua casa\nMas a vizinhança inteira\n\nE há tempos nem os santos\nTêm ao certo a medida da maldade\nE há tempos são os jovens que adoecem\nE há tempos o encanto está ausente\nE há ferrugem nos sorrisos\nSó o acaso estende os braços\nA quem procura abrigo e proteção\nMeu amor!\n\nDisciplina é liberdade\nCompaixão é fortaleza\nTer bondade é ter coragem\nLá em casa tem um poço\nMas a água é muito limpa'),(3,'PRA NÃO DIZER QUE NÃO FALEI DAS FLORES ','Caminhando e cantando\nE seguindo a canção\nSomos todos iguais\nBraços dados ou não\nNas escolas, nas ruas\nCampos, construções\nCaminhando e cantando\nE seguindo a canção\n\nVem, vamos embora\nQue esperar não é saber\nQuem sabe faz a hora\nNão espera acontecer\n\nVem, vamos embora\nQue esperar não é saber\nQuem sabe faz a hora\nNão espera acontecer\n\nPelos campos há fome\nEm grandes plantações\nPelas ruas marchando\nIndecisos cordões\nAinda fazem da flor\nSeu mais forte refrão\nE acreditam nas flores\nVencendo o canhão\n\nVem, vamos embora\nQue esperar não é saber\nQuem sabe faz a hora\nNão espera acontecer\n\nVem, vamos embora\nQue esperar não é saber\nQuem sabe faz a hora\nNão espera acontecer\n\nHá soldados armados\nAmados ou não\nQuase todos perdidos\nDe armas na mão\nNos quartéis lhes ensinam\nUma antiga lição\nDe morrer pela pátria\nE viver sem razão\n\nVem, vamos embora\nQue esperar não é saber\nQuem sabe faz a hora\nNão espera acontecer\n\nVem, vamos embora\nQue esperar não é saber\nQuem sabe faz a hora\nNão espera acontecer\n\nNas escolas, nas ruas\nCampos, construções\nSomos todos soldados\nArmados ou não\nCaminhando e cantando\nE seguindo a canção\nSomos todos iguais\nBraços dados ou não\n\nOs amores na mente\nAs flores no chão\nA certeza na frente\nA história na mão\nCaminhando e cantando\nE seguindo a canção\nAprendendo e ensinando\nUma nova lição\n\nVem, vamos embora\nQue esperar não é saber\nQuem sabe faz a hora\nNão espera acontecer\n\nVem, vamos embora\nQue esperar não é saber\nQuem sabe faz a hora\nNão espera acontecer'),(4,'ME DEIXA','Podem avisar, pode avisar\nInvente uma doença que me deixe em casa pra sonhar\nPode avisar, podem avisar\nInvente uma doença que me deixe em casa pra sonhar\n\nCom o novo enredo, com outro dia de folia\nCom o novo enredo, com outro dia de folia\n\nEu ia explodir, eu ia explodir\nMas eles não vão ver os meus pedaços por aí\nEu ia explodir, eu ia explodir\nMas eles não vão ver os meus pedaços por aí\n\nMe deixa\nQue hoje eu tô de bobeira, bobeira\nMe deixa\nQue hoje eu tô de bobeira, bobeira\n\nHoje eu desafio o mundo sem sair da minha casa\nHoje eu sou um homem mais sincero e mais justo comigo\nHoje eu desafio o mundo sem sair da minha casa\nHoje eu sou um homem mais sincero e mais justo comigo\n\nPodem os homem vir, que não vão me abalar\nOs cães farejam o medo, logo, não vão me encontrar\nNão se trata de coragem, mas meus olhos estão distantes\nMe camuflam na paisagem dando um tempo, tempo, tempo pra cantar\n\nMe deixa\nQue hoje eu tô de bobeira, bobeira\nMe deixa, deixa, deixa\nQue hoje eu tô de bobeira, bobeira\n\nHoje eu desafio o mundo sem sair da minha casa\nHoje eu sou um homem mais sincero e mais justo comigo\nHoje eu desafio o mundo sem sair da minha casa\nHoje eu sou um homem mais sincero e mais justo comigo\n\nPodem os homem vir, que não vão me abalar\nOs cães farejam o medo, logo, não vão me encontrar\nNão se trata de coragem, mas meus olhos estão distantes\nMe camuflam na paisagem dando um tempo, tempo, tempo pra cantar\n\nMe deixa\nQue hoje eu tô de bobeira, bobeira\nMe deixa\nQue hoje eu tô de bobeira, bobeira\n\nMe deixa, deixa, deixa\nQue hoje eu tô de bobeira, bobeira\nMe deixa, vê se me deixa\nQue hoje eu tô de bobeira, bobeira\n\nEu tô de bobeira\nEu tô de bobeira\nEu tô de bobeira\nBobeira\n\n'),(5,'CODINOME BEIJA FLOR','Pra que mentir\nFingir que perdoou\nTentar ficar amigos sem rancor\nA emoção acabou\nQue coincidência é o amor\nA nossa música nunca mais tocou\n\nPra que usar de tanta educação\nPra destilar terceiras intenções\nDesperdiçando o meu mel\nDevagarzinho, flor em flor\nEntre os meus inimigos, Beija-flor\n\nEu protegi o teu nome por amor\nEm um codinome, Beija-flor\nNão responda nunca, meu amor, nunca\nPra qualquer um na rua, Beija-flor\n\nQue só eu que podia\nDentro da tua orelha fria\nDizer segredos de liquidificador\n\nVocê sonhava acordada\nUm jeito de não sentir dor\nPrendia o choro e aguava o bom do amor\nPrendia o choro e aguava o bom do amor'),(6,'METAMORFOSE AMBULANTE','Prefiro ser\nEssa metamorfose ambulante\nEu prefiro ser\nEssa metamorfose ambulante\n\nDo que ter aquela velha opinião\nFormada sobre tudo\nDo que ter aquela velha opinião\nFormada sobre tudo\n\nEu quero dizer\nAgora o oposto do que eu disse antes\nEu prefiro ser\nEssa metamorfose ambulante\n\nDo que ter aquela velha opinião\nFormada sobre tudo\nDo que ter aquela velha opinião\nFormada sobre tudo\n\nSobre o que é o amor\nSobre o que eu nem sei quem sou\n\nSe hoje eu sou estrela\nAmanhã já se apagou\nSe hoje eu te odeio\nAmanhã lhe tenho amor\n\nLhe tenho amor\nLhe tenho horror\nLhe faço amor\nEu sou um ator\n\nÉ chato chegar\nA um objetivo num instante\nEu quero viver\nNessa metamorfose ambulante\n\nDo que ter aquela velha opinião\nFormada sobre tudo\nDo que ter aquela velha opinião\nFormada sobre tudo\n\nSobre o que é o amor\nSobre o que eu nem sei quem sou\n\nSe hoje eu sou estrela\nAmanhã já se apagou\nSe hoje eu te odeio\nAmanhã lhe tenho amor\n\nLhe tenho amor\nLhe tenho horror\nLhe faço amor\nEu sou um ator\n\nEu vou desdizer\nAquilo tudo que eu lhe disse antes\nEu prefiro ser\nEssa metamorfose ambulante\n\nDo que ter aquela velha opinião\nFormada sobre tudo\nDo que ter aquela velha opinião\nFormada sobre tudo\n\n'),(7,'ROBOCOPY GAY','Um tanto quanto másculo\nAi, com M maiúsculo\nVejam só os meus músculos\nQue com amor cultivei\n\nMinha pistola é de plástico (quero chupar-pa-pa)\nEm formato cilíndrico (quero chupar-pa)\nSempre me chamam de cínico (quero chupar)\nMas o porquê eu não sei (quero chupar-pa)\n\nO meu bumbum era flácido\nMas esse assunto é tão místico\nDevido a um ato cirúrgico\nHoje eu me transformei\n\nO meu andar é erótico (silicone, yeah, yeah, yeah)\nCom movimentos atômicos (silicone, yeah, yeah)\nSou um amante robótico (silicone, yeah)\nCom direito a replay (silicone, yeah)\n\nUm ser humano fantástico\nCom poderes titânicos\nFoi um moreno simpático\nPor quem me apaixonei\n\nE hoje estou tão eufórico (doce, doce, amor)\nCom mil pedaços biônicos (doce, doce, amor)\nOntem eu era católico (doce, doce, amor)\nAi, hoje eu sou um gay!\n\nAbra sua mente\nGay também é gente\nBaiano fala: Oxente\nE come vatapá\n\nVocê pode ser gótico\nSer punk ou skinhead\nTem gay que é Mohamed\nTentando camuflar\nAlá, meu bom Alá\n\nFaça bem a barba\nArranque seu bigode\nGaúcho também pode\nNão tem que disfarçar\n\nFaça uma plástica\nAi, entre na ginástica\nBoneca cibernética\nUm Robocop Gay\nUm Robocop Gay\n\nUm Robocop Gay\nAi, eu sei, eu sei\nMeu Robocop Gay\n\nAi, como dói'),(8,'SÁBADO DE SOL','Sábado de sol\nAluguei um caminhão\nPra levar a galera\nPra comer feijão\n\nChegando lá\nMas que vergonha\nSó tinha maconha\nOs maconheiros tava doidão\nQuerendo o meu feijão\n\nSábado de sol\nAluguei um caminhão\nPra levar a galera\nPra comer feijão\n\nChegando lá\nMas que vergonha\nSó tinha maconha\nOs maconheiros tava doidão\nQuerendo o meu feijão\n(Porra, meu! Não dou mesmo!)\n\n'),(9,'PAIS E FILHOS','Estátuas e cofres e paredes pintadas\nNinguém sabe o que aconteceu\nEla se jogou da janela do quinto andar\nNada é fácil de entender\n\nDorme agora\nÉ só o vento lá fora\n\nQuero colo! Vou fugir de casa\nPosso dormir aqui com vocês?\nEstou com medo, tive um pesadelo\nSó vou voltar depois das três\n\nMeu filho vai ter nome de santo\nQuero o nome mais bonito\n\nÉ preciso amar as pessoas\nComo se não houvesse amanhã\nPorque se você parar pra pensar\nNa verdade não há\n\nMe diz, por que que o céu é azul?\nExplica a grande fúria do mundo\nSão meus filhos\nQue tomam conta de mim\n\nEu moro com a minha mãe\nMas meu pai vem me visitar\nEu moro na rua, não tenho ninguém\nEu moro em qualquer lugar\n\nJá morei em tanta casa\nQue nem me lembro mais\nEu moro com os meus pais\n\nÉ preciso amar as pessoas\nComo se não houvesse amanhã\nPorque se você parar pra pensar\nNa verdade não há\n\nSou uma gota d\água\nSou um grão de areia\nVocê me diz que seus pais não te entendem\nMas você não entende seus pais\n\nVocê culpa seus pais por tudo, isso é absurdo\nSão crianças como você\nO que você vai ser\nQuando você crescer\n\n'),(10,'Quando o Sol Bater na Janela do Teu Quarto','Quando o sol bater\nNa janela do teu quarto\nLembra e vê\nQue o caminho é um só\n\nPor que esperar\nSe podemos começar\nTudo de novo?\nAgora mesmo\n\nA humanidade é desumana\nMas ainda temos chance\nO sol nasce pra todos\nSó não sabe quem não quer\n\nQuando o sol bater\nNa janela do teu quarto\nLembra e vê\nQue o caminho é um só\n\nAté bem pouco tempo atrás\nPoderíamos mudar o mundo\nQuem roubou nossa coragem?\n\nTudo é dor\nE toda dor vem do desejo\nDe não sentirmos dor\n\nQuando o sol bater\nNa janela do teu quarto\nLembra e vê\nQue o caminho é um só'),(11,'LUTAR PELO QUE É MEU','A gente passa a entender melhor a vida\nQuando encontra o verdadeiro amor\nCada escolha, uma renúncia, isso é a vida\nEstou lutando pra me recompor\n\nDe qualquer jeito seu sorriso vai ser meu raio de sol\nDe qualquer jeito seu sorriso vai ser meu raio de sol\n\nO melhor presente Deus me deu\nA vida me ensinou a lutar pelo que é meu\nO melhor presente Deus me deu\nA vida me ensinou a lutar pelo que é meu\n\nEntão deixa eu te beijar até você sentir vontade de tirar a roupa\nDeixa eu acompanhar esse instinto de aventura, de menina solta\nDeixa minha estrela orbitar e brilhar no céu da sua boca\nDeixa eu te mostrar que a vida pode ser melhor, mesmo sendo tão louca\n\nDe qualquer jeito seu sorriso vai ser meu raio de sol\nDe qualquer jeito seu sorriso vai ser meu raio de sol\n\nO melhor presente Deus me deu\nA vida me ensinou a lutar pelo que é meu\nO melhor presente Deus me deu\nA vida me ensinou a lutar pelo que é meu\n\nStop\nThey don\t love you like I love you\nStop\nThey don\t love you like I love you\nStop\nYeah, yeah man\n\nA gente passa a entender melhor a vida\nQuando encontra o verdadeiro amor\nCada escolha, uma renúncia, isso é a vida\nEstou lutando pra me recompor\n\nDe qualquer jeito seu sorriso vai ser meu raio de sol\nDe qualquer jeito seu sorriso vai ser meu raio de sol\n\nO melhor presente Deus me deu\nA vida me ensinou a lutar pelo que é meu\nO melhor presente Deus me deu\nA vida me ensinou a lutar pelo que é meu\n\nO melhor presente Deus me deu\nA vida me ensinou a lutar pelo que é meu\nO melhor presente Deus me deu\nA vida me ensinou a lutar pelo que é meu'),(12,'ELA VAI VOLTAR','Minha mente\nNem sempre tão lúcida\nÉ fértil e me deu a voz\nMinha mente\nNem sempre tão lúcida\nFez ela se afastar\nMas ela vai voltar\nMas ela vai voltar\n\nEla não é\nDo tipo de mulher\nQue se entrega na primeira\nMas melhora na segunda\nE o paraíso é na terceira\nEla tem força\nEla tem sensibilidade\nEla é guerreira\nEla é uma deusa\nEla é mulher de verdade\n\nEla é daquelas\nQue tu gosta na primeira\nSe apaixona na segunda\nE perde a linha na terceira\nEla é discreta\nE cultua bons livros\nE ama os animais\nTá ligado, eu sou o bicho\n\nMinha mente\nNem sempre tão lúcida\nÉ fértil e me deu a voz\nMinha mente\nNem sempre tão lúcida\nFez ela se afastar\nMas ela vai voltar\nMas ela vai voltar\n\nDeixa eu te levar\nPra ver o mundo, baby\nDeixa eu te mostrar\nO melhor que eu posso ser\n\nDeixa eu te levar\nPra ver o mundo, baby\nDeixa eu te mostrar\nO melhor que eu posso ser\n\nFazer da vida\nO que melhor possa ser\nTraçar um rumo novo\nEm direção ao sol\nMe sinto muito bem\nQuando vejo o pôr-do-sol\nSó pra fazer nascer a lua\n\nMinha mente\nNem sempre tão lúcida\nÉ fértil e me deu a voz\nMinha mente\nNem sempre tão lúcida\nFez ela se afastar\nMas ela vai voltar\nMas ela vai voltar\nMas ela vai voltar\nMas ela vai voltar'),(13,'SENHOR DO TEMPO','Eu não sou o senhor do tempo, mas eu sei que vai chover\nMe sinto muito bem quando fico com você\nEu tenho habilidade de fazer histórias tristes\nVirarem melodia, vou vivendo o dia a dia\n\nNa paz, na moral, na humilde, busco só sabedoria\nAprendendo todo dia, me espelho em você\nCorro junto com você, vivo junto com você\nFaço tudo por você\n\nSeguindo em frente com fé e atenção\nContinuo na missão continuo por você e por mim\nPorque quando a casa cai\nNão dá pra fraquejar, quem é guerreiro tá ligado\nQue guerreiro é assim\n\nO tempo passa e um dia a gente aprende\nHoje eu sei realmente o que faz a minha mente\nEu vi o tempo passar, vi pouca coisa mudar\nEntão tomei um caminho diferente\nTanta gente equivocada faz mau uso da palavra\nFalam, falam o tempo todo, mas não tem nada a dizer\nMas eu tenho santo forte, é incrível a minha sorte\nAgradeço todo tempo por ter encontrado você\n\nO tempo é rei, e a vida é uma lição\nE um dia a gente cresce\nE conhece nossa essência e ganha experiência\nE aprende o que é raiz, então cria consciência\n\nTem gente que reclama da vida o tempo todo\nMas a lei da vida é quem dita o fim do jogo\nEu vi de perto o que neguinho é capaz por dinheiro\nEu conheci o próprio lobo na pele de um cordeiro\nInfelizmente a gente tem que tá ligado o tempo inteiro\nLigado nos pilantra e também nos bagunceiro\nE a gente se pergunta por que a vida é assim?\nÉ difícil pra você e é difícil pra mim\n\nEu não sou o senhor do tempo, mas eu sei que vai chover\nMe sinto muito bem quando fico com você\nEu tenho habilidade de fazer histórias tristes\nVirarem melodia vou vivendo o dia-a-dia\nNa paz, na moral, na humilde busco só sabedoria\nAprendendo todo dia me espelho em você\nCorro junto com você, vivo junto com você, faço tudo por você\n\nVivendo nesse mundo louco hoje só na brisa\nViver pra ser melhor também é jeito de levar a vida\n\nO tempo passa e um dia a gente aprende\nHoje eu sei realmente o que faz a minha mente\nEu vi o tempo passar e pouca coisa mudar\nEntão tomei um caminho diferente\nTanta gente equivocada faz mal uso da palavra\nFalam, falam o tempo todo mas não tem nada a dizer\nMas eu tenho santo forte é incrível a minha sorte\nAgradeço todo tempo ter encontrado você\n\nVem que o bom astral vai dominar o mundo!\nEu já briguei com a vida, hoje eu vivo bem com tudo mundo aí\nNa maior moral Charlie Brown!\nVivendo nesse mundo louco hoje só na brisa\nViver pra ser melhor também é um jeito de levar a vida');
